<script>
     function toggleView(type) {   
        $(".dna-files").hide();
        if(type=='video360') {   
            $("#video_div").show();
            if(type=='video360') {   
                $("#btn_video360").removeClass('btn-gray').addClass('btn-selected');
            } else  {   
                $("#btn_video360").addClass('btn-gray').removeClass('btn-selected');
            }
        } else if(type=='stillImage') {   
            $("#image_div").show();
            if(type=='stillImage') {   
                $("#btn_stillImage").removeClass('btn-gray').addClass('btn-selected');
            } else  {   
                $("#btn_stillImage").addClass('btn-gray').removeClass('btn-selected');
            }
        }
        $("#"+type).show();
     }
</script>
<script>
    function download(type,stock)  {    

            $.ajax({
            url: base_url+'dna-download-file',
            dataType: 'json',
            type: 'get',            
            data: { "stock": stock,"type": type },            
            success: function(data){  
             
   } 
});
}
